import React from 'react';
import '../index.css';
import {  validateEmail } from '../utils/helpers';

function About () {


function Form() {
  // Create state variables for the fields in the form
  // We are also setting their initial values to an empty string
  const [email, setEmail] = useState('');
  const [name, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleInputChange = (e) => {
    // Getting the value and name of the input which triggered the change
    const { target } = e;
    const inputType = target.name;
    const inputValue = target.value;

    // Based on the input type, we set the state of either email, username, and password
    if (inputType === 'email') {
      setEmail(inputValue);
    } else if (inputType === 'name') {
      setName(inputValue);
    } else {
      setMessage(inputValue);
    }
  };

  const handleFormSubmit = (e) => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    e.preventDefault();

    // First we check to see if the email is not valid
    if (!validateEmail(email)) {
      setErrorMessage('Email is invalid');
      // We want to exit out of this code block if something is wrong so that the user can correct it
      return;

    }



    // If everything goes according to plan, we want to clear out the input after a successful registration.
    setName('');
    setMessage('');
    setEmail('');
  };

    return (
        <section className="about" id="about">
        <div className="row">
          <h2>About Me</h2>
          <div className="about__content">
            <div className="about__text">

              <p>
                  Hi! My name is Lachlan and I am from Adelaide in South Australia. I enrolled in the University of 
                  Adelaide's coding bootcamp and started my course on the 29th of November 2021. <br></br>
                  Since starting the bootcamp it has been a very challenging learning journey but also very exciting and 
                  rewarding.<br></br> When i'm not spending time working on my coding skills I spend most my time either watching
                  sports, or cycling
              </p>

              <a href={"Lachlan.ConnellResume.pdf"} className="btn">My Resume</a>
            </div>

            <div className="about__photo-container">
              <img className="about__photo" src={"./assets/images/me.png"} alt="Me" />
            </div>
          </div>
        </div>
        <a href="#top" class="back-to-top" title="Back to Top">
       <img src={"./assets/images/arrow-up.svg"} alt="Back to Top" className="back-to-top__image"/>
     </a>
     <div>
      <form className="form">
      <input
          value={name}
          name="name"
          onChange={handleInputChange}
          type="text"
          placeholder="name"
        />
        <input
          value={email}
          name="email"
          onChange={handleInputChange}
          type="email"
          placeholder="email"
        />
        <input
          value={message}
          name="message"
          onChange={handleInputChange}
          type="text"
          placeholder="message"
        />
        <button type="button" onClick={handleFormSubmit}>Submit</button>
      </form>
      {errorMessage && (
        <div>
          <p className="error-text">{errorMessage}</p>
        </div>
      )}
    </div>
      </section>
    );
}
}
export default About;
